-- Drop the table if it exists
DROP TABLE IF EXISTS ao_clientes;

-- Create the table
CREATE TABLE ao_clientes (
    numero VARCHAR PRIMARY KEY,
    departamento VARCHAR,
    data JSONB DEFAULT '{}',
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- Create trigger function to automatically update updated_at
CREATE OR REPLACE FUNCTION update_updated_at_column_ao_clientes()
RETURNS TRIGGER AS $
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END;
$ language 'plpgsql';

-- Create trigger that calls the function before each update
CREATE TRIGGER update_updated_at_column_ao_clientes
    BEFORE UPDATE ON ao_clientes
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column_ao_clientes();

