const getAvailableSlots = () => {
    // Configura√ß√µes
    const startHour = 9; // Hor√°rio inicial (09:00) no hor√°rio de S√£o Paulo
    const endHour = 18; // Hor√°rio final (18:00) no hor√°rio de S√£o Paulo
    const interval = 1; // Intervalo de 1 hora
    const businessDays = 5; // Pr√≥ximos 5 dias √∫teis
    const now = new Date(); // Data e hora atual

    // Fun√ß√£o para verificar se um dia √© √∫til (seg. a sex.)
    const isBusinessDay = (date) => {
        const day = date.getDay();
        return day !== 0 && day !== 6; // 0 = domingo, 6 = s√°bado
    };

    // Fun√ß√£o para criar um hor√°rio em UTC a partir do hor√°rio de S√£o Paulo
    const toUTCTime = (date, hour) => {
        const utcDate = new Date(date);
        utcDate.setHours(hour + 3, 0, 0, 0); // Adiciona 3 horas para converter UTC-3 para UTC
        return utcDate;
    };

    // Incrementa a data at√© encontrar os pr√≥ximos 5 dias √∫teis
    let date = new Date(now);
    const slots = [];

    for (let i = 0; i < businessDays; ) {
        if (isBusinessDay(date)) {
            // Adiciona os hor√°rios dispon√≠veis do dia √∫til atual no hor√°rio de S√£o Paulo
            for (let hour = startHour; hour < endHour; hour += interval) {
                const slot = toUTCTime(date, hour); // Converte o hor√°rio para UTC
                if (slot > now) {
                    slots.push(slot.toISOString());
                }
            }
            i++;
        }
        // Incrementa o dia
        date.setDate(date.getDate() + 1);
    }

    // Retorna os slots no formato esperado pelo n8n
    return slots.map((slot) => ({ json: { availableSlot: slot } }));
};

// Chama a fun√ß√£o e retorna os resultados
