const inputData = $input.all()[0].json;

// Debug: Exibir os dados recebidos para verificar estrutura
console.log("Dados recebidos:", JSON.stringify(inputData, null, 2));

// Fun√ß√£o para converter hor√°rios para UTC (normaliza todos para o mesmo formato)
const toUTC = (dateString) => {
  if (!dateString || typeof dateString !== "string" || isNaN(Date.parse(dateString))) {
    console.error(`Erro: Data inv√°lida recebida: ${dateString}`);
    return null; // Retorna null em vez de lan√ßar erro para evitar crash
  }
  return new Date(dateString).toISOString(); // Normaliza para UTC
};

// Verifica se os arrays existem antes de process√°-los
if (!Array.isArray(inputData.horarios_agendados)) {
  console.error("Erro: 'horarios_agendados' n√£o √© um array ou est√° ausente.");
  return [];
}
if (!Array.isArray(inputData.horarios_futuros)) {
  console.error("Erro: 'horarios_futuros' n√£o √© um array ou est√° ausente.");
  return [];
}

// Extrai e normaliza os hor√°rios agendados para UTC
const scheduledEvents = inputData.horarios_agendados.map(event => {
  if (!event || !event.start || !event.start.dateTime || !event.end || !event.end.dateTime) {
    console.error(`Erro: Evento inv√°lido encontrado: ${JSON.stringify(event)}`);
    return null; // Evita crash caso um evento esteja com dados ausentes
  }

  const startUTC = toUTC(event.start.dateTime);
  const endUTC = toUTC(event.end.dateTime);

  if (!startUTC || !endUTC) return null; // Se houver erro, ignora este evento

  return { start: startUTC, end: endUTC };
}).filter(event => event !== null); // Remove eventos inv√°lidos

// Extrai os hor√°rios futuros (j√° est√£o em UTC)
const futureSlots = inputData.horarios_futuros.map(slot => {
  if (!slot || !slot.availableSlot || isNaN(Date.parse(slot.availableSlot))) {
    console.error(`Erro: Hor√°rio futuro inv√°lido: ${JSON.stringify(slot)}`);
    return null;
  }
  return { availableSlot: slot.availableSlot, original: slot };
}).filter(slot => slot !== null); // Remove slots inv√°lidos

// Define a dura√ß√£o dos hor√°rios futuros (em milissegundos)
const SLOT_DURATION = 60 * 60 * 1000; // 1 hora

// Fun√ß√£o para verificar se um hor√°rio est√° livre
const isSlotAvailable = (slot) => {
  const slotStart = new Date(slot.availableSlot);
  const slotEnd = new Date(slotStart.getTime() + SLOT_DURATION); // Calcula o final do slot

  return !scheduledEvents.some(event => {
    const eventStart = new Date(event.start);
    const eventEnd = new Date(event.end);

    // Verifica se h√° sobreposi√ß√£o entre o slot e o evento
    return slotStart < eventEnd && slotEnd > eventStart;
  });
};

// Filtra os hor√°rios dispon√≠veis
const filteredSlots = futureSlots
  .filter(isSlotAvailable)
  .map(slot => ({
    json: {
      ...slot.original,
      availableSlot: slot.availableSlot
    }
  }));

// Debug: Exibir os hor√°rios filtrados antes de retornar
console.log("Hor√°rios dispon√≠veis:", JSON.stringify(filteredSlots, null, 2));

// Retorna os resultados no formato esperado pelo n8n
return filteredSlots;

