DO $
BEGIN
    IF EXISTS (SELECT FROM pg_tables WHERE schemaname = 'public' AND tablename = 'advogai_clientes') THEN
        DELETE FROM advogai_clientes;
    END IF;
END $;

-- Drop the table if it exists
DROP TABLE IF EXISTS advogai_clientes;

-- Create the table
CREATE TABLE advogai_clientes (
    cpf VARCHAR PRIMARY KEY,
    numero_processo VARCHAR,
    data JSONB DEFAULT '{}',
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- Create trigger function to automatically update updated_at
CREATE OR REPLACE FUNCTION update_updated_at_column_advogai_clientes()
RETURNS TRIGGER AS $
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END;
$ language 'plpgsql';

-- Create trigger that calls the function before each update
CREATE TRIGGER update_updated_at_column_advogai_clientes
    BEFORE UPDATE ON advogai_clientes
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column_advogai_clientes();
